import { useState } from 'react'

const App = () => {
  const [ counter, setCounter ] = useState(0)

  let test = (number) => setCounter(number)

  console.log('rendering...', counter)

  return (
    <div><button onClick={test(counter+1)}>{counter}</button></div>
  )
}

export default App
